def EnumChildProc(hwnd, lParam):
    buffering = PyMakeBuffer(255)
    length = SendMessage(hwnd, WM_GETTEXT, 255, buffering)
    result = str(buffering[0:length*2].tobytes().decode('utf-16'))
    result = result[::-1]
    SendMessage(hwnd, WM_SETTEXT, None, result)

